import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [owners, setOwners] = useState([]);

  // Function to fetch data from the backend
  const fetchOwners = async () => {
    try {
      const response = await axios.get('YOUR_BACKEND_API_URL');
      setOwners(response.data);
    } catch (error) {
      console.error('Error fetching owners:', error);
    }
  };

  // Fetch owners data from backend when component mounts
  useEffect(() => {
    fetchOwners();
  }, []);

  // Function to handle cell editing
  const handleCellEdit = (value, rowIndex, columnName) => {
    const updatedOwners = [...owners];
    updatedOwners[rowIndex][columnName] = value;
    setOwners(updatedOwners);
  };

  return (
    <div>
      <h1>Owners</h1>
      <table>
        <thead>
          <tr>
            <th>Flat Number</th>
            <th>Name</th>
            <th>Address</th>
            <th>Mobile Number</th>
            <th>Email</th>
          </tr>
        </thead>
        <tbody>
          {owners.map((owner, index) => (
            <tr key={index}>
              <EditableCell
                value={owner.flatNo}
                onChange={(value) => handleCellEdit(value, index, 'flatNo')}
              />
              <EditableCell
                value={owner.name}
                onChange={(value) => handleCellEdit(value, index, 'name')}
              />
              <EditableCell
                value={owner.address}
                onChange={(value) => handleCellEdit(value, index, 'address')}
              />
              <EditableCell
                value={owner.mobileNo}
                onChange={(value) => handleCellEdit(value, index, 'mobileNo')}
              />
              <EditableCell
                value={owner.email}
                onChange={(value) => handleCellEdit(value, index, 'email')}
              />
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

// Editable cell component remains the same

export default App;
