import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './announcementTable.css';

const AnnouncementsTable = () => {
    const [announcements, setAnnouncements] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        // Fetch announcements data when the component mounts
        fetchAnnouncements();
    }, []);

    const fetchAnnouncements = async () => {
        try {
            const response = await axios.get('http://localhost:9898/announcements'); // Adjust the URL accordingly
            setAnnouncements(response.data.data);
            setLoading(false);
        } catch (error) {
            setError(error);
            setLoading(false);
        }
    };

    return (
        <div className="announcementTable-card">
            <h2 className="announcementTable-title">Announcements Table</h2>
            {loading ? (
                <div>Loading...</div>
            ) : error ? (
                <div>Error: {error.message}</div>
            ) : (
                <div className="announcementTable-container">
                    <table className="announcementTable-table">
                        <thead>
                            <tr>
                                <th>Announcement ID</th>
                                <th>Announcement</th>
                                <th>Created At</th>
                            </tr>
                        </thead>
                        <tbody>
                            {announcements.map(announcement => (
                                <tr key={announcement.id}>
                                    <td>{announcement.id}</td>
                                    <td>{announcement.announcement}</td>
                                    <td>{announcement.created_at}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
};

export default AnnouncementsTable;
