import { DataGrid } from '@mui/x-data-grid';
import DeleteIcon from '@mui/icons-material/Delete';
import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from 'axios';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import "./eventList.css"; // assuming you have eventList.css for styling
import config from '../../config/config';

export default function EventList() {
    const [data, setData] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(config.server+'/event');
                setData(response.data.data.map(event => ({
                    ...event,
                    id: event.id.toString(), // Ensure ID is string for DataGrid
                })));
            } catch (error) {
                console.error('Error fetching events:', error);
                toast.error("Error Fetching Events");
            }
        };
        fetchData();
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:4001/event/delete/${id}`);
            setData(data.filter((item) => item.id !== id));
            toast.success("Event Deleted Successfully");
        } catch (error) {
            console.error('Error deleting event:', error);
            toast.error("Error Deleting Event");
        }
    };

    const columns = [
        { field: 'id', headerName: 'ID', width: 70 },
        {
            field: "name",
            headerName: "Name",
            width: 200,
        },
        { field: 'details', headerName: 'Details', width: 200 },
        {
            field: 'image',
            headerName: 'Image',
            width: 130,
            renderCell: (params) => (
                <a href={"http://localhost:4001/" + params.value} target="_blank" rel="noopener noreferrer">
                    <img
                        src={"http://localhost:4001/" + params.value}
                        alt={`Event ${params.row.id}`}
                        style={{ width: 100, height: 100, objectFit: 'cover' }}
                    />
                </a>
            )
        },

        {
            field: 'createdTimestamp',
            headerName: 'Created Timestamp',
            width: 200,
        },
        {
            field: 'action',
            headerName: 'Action',
            width: 200,
            renderCell: (params) => (
                <div className="actionButtons">
                    <Link to={`/event/id/${params.row.id}`}>
                        <button className="eventListEdit">Edit</button>
                    </Link>
                    <DeleteIcon className="eventListDelete" onClick={() => handleDelete(params.row.id)} />
                </div>
            )
        },
    ];

    return (
        <div className="eventList">
            <h1 className="eventListTitle">Event List</h1>
            <DataGrid
                rows={data}
                columns={columns}
                pageSize={5}
                checkboxSelection
                disableSelectionOnClick
            />
        </div>
    );
}
