import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom'; // Import useHistory
import './ownerHome.css';

function OwnerHome() {
    const [ownerInfo, setOwnerInfo] = useState(null);

    useEffect(() => {
        const storedOwnerInfo = localStorage.getItem('ownerInfo');
        if (storedOwnerInfo) {
            setOwnerInfo(JSON.parse(storedOwnerInfo));
        }
    }, []);

    return (
        <>
        <div className="owner-home-container">
            <h2>Owner Information</h2>
            <div className="owner-card">
                {ownerInfo ? (
                    <div>
                        <p>Flat No: {ownerInfo.flat_no}</p>
                        <p>Name: {ownerInfo.name}</p>
                        <p>Address: {ownerInfo.address}</p>
                        <p>Mobile No: {ownerInfo.mobile_no}</p>
                        <p>Email: {ownerInfo.email}</p>
                    </div>
                ) : (
                    <p>Loading owner info...</p>
                )}
            </div>

        </div>

        <div className="owner-home-container">
            <div className="owner-card2">
                <img src="https://cdn.vectorstock.com/i/preview-1x/18/46/amenities-concept-blue-line-icon-simple-thin-vector-27321846.jpg" alt="Owner" />
            </div>




            <div className="owner-card2">
                <img src="https://cdn.forumcomm.com/dims4/default/aa6899c/2147483647/strip/true/crop/4436x2957+160+0/resize/840x560!/format/webp/quality/90/?url=https%3A%2F%2Fforum-communications-production-web.s3.us-west-2.amazonaws.com%2Fbrightspot%2Fef%2F01%2Fdde07d5540038af08eea80820ea6%2Fannouncement-graphic-2.jpg" alt="Owner" />
            </div>

            </div>

        </>
    );
}

export default OwnerHome;
