import { useEffect, useRef, useState } from 'react';
import './secretaryHome.css'; // Import your CSS file
import { useHistory } from 'react-router-dom';

import OwnerTable from '../table/ownerTable';

export default function SecretaryHome() {
    const [showPopup, setShowPopup] = useState(false);
    const popupRef = useRef(null);
    const history = useHistory();

    useEffect(() => {
        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    const togglePopup = () => {
        setShowPopup(!showPopup);
    };

    const handleAddAnnouncement = () => {
        history.push('/register-society');
    };

    const handleAddAmenities = () => {
        history.push('/register-secretary');
    };

    const handleRegisterOwner = () => {
        history.push('/registerOwner');
    };

    const handleClickOutside = (event) => {
        if (popupRef.current && !popupRef.current.contains(event.target)) {
            setShowPopup(false);
        }
    };

    return (
        <>
            <div className="secretary-home-container">
                <div className="secretary-home-card">
                    <h2>Flats</h2>
                    <p></p>
                </div>

                <div className="secretary-home-card" onClick={togglePopup}>
                    <h2>Add</h2>
                    <img src="https://static.vecteezy.com/system/resources/previews/013/215/442/non_2x/add-button-black-glyph-ui-icon-circle-with-plus-website-interactive-element-user-interface-design-silhouette-symbol-on-white-space-solid-pictogram-for-web-mobile-isolated-illustration-vector.jpg" alt="Register" />
                </div>

                {showPopup && (
                    <div className="secretary-home-popup" ref={popupRef}>
                        <div className="secretary-home-popup-inner">
                            <h2>Options</h2>
                            <button onClick={handleRegisterOwner} className="secretary-home-register-owner">Register Owner</button>
                            <button onClick={handleAddAmenities} className="secretary-home-add-amenities">Add Amenities</button>
                            <button onClick={handleAddAnnouncement} className="secretary-home-add-announcement">Add Announcement</button>
                        </div>
                    </div>
                )}

                <div className="secretary-home-card">
                    <h2>No of Members</h2>
                    <p></p>
                </div>
            </div>

            <OwnerTable />
        </>
    );
}
