import React, { useState } from 'react';
import axios from 'axios';
import './registerOwner.css'
import { useHistory } from 'react-router-dom/cjs/react-router-dom.min';

function RegisterOwner() {
    // State to manage form data
    const [ownerData, setOwnerData] = useState({
        flat_no: '',
        name: '',
        address: '',
        mobile_no: '',
        email: '',
        password: ''
    });

    // Function to handle changes to form inputs
    const handleChange = (e) => {
        const { name, value } = e.target;
        setOwnerData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    // Function to handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Send form data using Axios
            const response = await axios.post('YOUR_API_ENDPOINT', ownerData);
            console.log('Owner data submitted successfully!', response.data);
            // Optionally, you can reset the form after successful submission
            setOwnerData({
                flat_no: '',
                name: '',
                address: '',
                mobile_no: '',
                email: '',
                password: ''
            });
        } catch (error) {
            console.error('Error submitting owner data:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                name="flat_no"
                placeholder="Flat Number"
                value={ownerData.flat_no}
                onChange={handleChange}
            />
            <input
                type="text"
                name="name"
                placeholder="Name"
                value={ownerData.name}
                onChange={handleChange}
            />
            <input
                type="text"
                name="address"
                placeholder="Address"
                value={ownerData.address}
                onChange={handleChange}
            />
            <input
                type="text"
                name="mobile_no"
                placeholder="Mobile Number"
                value={ownerData.mobile_no}
                onChange={handleChange}
            />
            <input
                type="email"
                name="email"
                placeholder="Email"
                value={ownerData.email}
                onChange={handleChange}
            />
            <input
                type="password"
                name="password"
                placeholder="Password"
                value={ownerData.password}
                onChange={handleChange}
            />
            <button type="submit">Submit</button>
        </form>
    );
}

export default RegisterOwner;
