// RegisterSecretary.jsx
import { useHistory } from 'react-router-dom'; // Import useHistory hook
import React, { useState } from 'react';
import './registerSecretary.css';
import Topbar from '../../components/topbar/Topbar';

const RegisterSecretary = () => {
    const [secretaryData, setSecretaryData] = useState({
        society_id: '',
        name: '',
        email: '',
        mobile_no: '',
        password: ''
    });

    const history = useHistory(); 

    const handleChange = (e) => {
        const { name, value } = e.target;
        setSecretaryData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        // You can handle form submission logic here
        console.log('Secretary Data:', secretaryData);
        // Reset form fields after submission
        setSecretaryData({
            society_id: '',
            name: '',
            email: '',
            mobile_no: '',
            password: ''
        });

        history.push('/adminDashboard');
    };

    return (
        <div className="register-secretary-container">
               <Topbar/>
            <h2 className='form-label'>Register Secretary</h2>
            <form onSubmit={handleSubmit} className="register-secretary-form">
                <div className="form-group">
                    <label htmlFor="society_id">Society ID</label>
                    <input 
                        type="text" 
                        id="society_id" 
                        name="society_id"
                        value={secretaryData.society_id} 
                        onChange={handleChange} 
                        required 
                        className="form-control"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="name">Name</label>
                    <input 
                        type="text" 
                        id="name" 
                        name="name"
                        value={secretaryData.name} 
                        onChange={handleChange} 
                        required 
                        className="form-control"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="email">Email</label>
                    <input 
                        type="email" 
                        id="email" 
                        name="email"
                        value={secretaryData.email} 
                        onChange={handleChange} 
                        required 
                        className="form-control"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="mobile_no">Mobile No</label>
                    <input 
                        type="text" 
                        id="mobile_no" 
                        name="mobile_no"
                        value={secretaryData.mobile_no} 
                        onChange={handleChange} 
                        required 
                        className="form-control"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Password</label>
                    <input 
                        type="password" 
                        id="password" 
                        name="password"
                        value={secretaryData.password} 
                        onChange={handleChange} 
                        required 
                        className="form-control"
                    />
                </div>
                <button type="submit" className="btn-submit">Register</button>
            </form>
        </div>
    );
};

export default RegisterSecretary;
