import React, { useState } from 'react';
import './registerSocietyForm.css';

const RegisterSociety = () => {
    const [societyData, setSocietyData] = useState({
        name: '',
        address: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setSocietyData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        // You can handle form submission logic here
        console.log('Society Data:', societyData);
        // Reset form fields after submission
        setSocietyData({
            name: '',
            address: ''
        });
    };

    return (
        <div className="register-society-container">
            <h2>Register Society</h2>
            <form onSubmit={handleSubmit} className="register-society-form">
                <div className="form-group">
                    <label htmlFor="name">Name</label>
                    <input 
                        type="text" 
                        id="name" 
                        name="name"
                        value={societyData.name} 
                        onChange={handleChange} 
                        required 
                        className="form-control"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="address">Address</label>
                    <textarea 
                        id="address" 
                        name="address"
                        value={societyData.address} 
                        onChange={handleChange} 
                        required 
                        className="form-control"
                    ></textarea>
                </div>
                <button type="submit" className="btn-submit">Register</button>
            </form>
        </div>
    );
};

export default RegisterSociety;
