import { DataGrid } from '@mui/x-data-grid';
import DeleteIcon from '@mui/icons-material/Delete';
import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from 'axios';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import "./venueList.css";
import config from '../../config/config';

export default function VenueList() {
    const [data, setData] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(config.server+'/venue');
                setData(response.data.data.map(venue => ({
                    ...venue,
                    id: venue.id.toString(), // Ensure ID is string for DataGrid
                })));
            } catch (error) {
                console.error('Error fetching venues:', error);
                toast.error("Error Fetching Venues");
            }
        };
        fetchData();
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:4001/venue/delete/${id}`);
            setData(data.filter((item) => item.id !== id));
            toast.success("Venue Deleted Successfully");
        } catch (error) {
            console.error('Error deleting venue:', error);
            toast.error("Error Deleting Venue");
        }
    };

    const columns = [
        { field: 'id', headerName: 'ID', width: 70 },
        {
            field: "name",
            headerName: "Name",
            width: 200,
        },
        { field: 'capacity', headerName: 'Capacity', width: 130 },
        { field: 'price', headerName: 'Price', width: 130 },
        { field: 'location', headerName: 'Location', width: 130 },
        {
            field: 'image',
            headerName: 'Image',
            width: 130,
            renderCell: (params) => (
                <a href={"http://localhost:4001/"+ params.value} target="_blank" rel="noopener noreferrer">
                    <img
                        src={"http://localhost:4001/"+ params.value}
                        alt={`Venue ${params.row.id}`}
                        style={{ width: 100, height: 100, objectFit: 'cover' }}
                    />
                </a>
            )
        },
        {
            field: 'action',
            headerName: 'Action',
            width: 200, // Increase the width to accommodate the buttons
            renderCell: (params) => (
                <div className="actionButtons">
                    <Link to={`/venue/id/${params.row.id}`}>
                        <button className="venueListEdit">Edit</button>
                    </Link>
                    <DeleteIcon className="venueListDelete" onClick={() => handleDelete(params.row.id)} />
                </div>
            )
        },
    ];

    return (
        <div className="venueList">
            <h1 className="addeventTitle">Venue List</h1>
            <DataGrid
                rows={data} disableRowSelectionOnClick
                columns={columns}
                pageSize={5}
                checkboxSelection
                disableSelectionOnClick
            />
        </div>
    );
}
