import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';
import './login.css'; // Import CSS file for styling
import Topbar from '../../components/topbar/Topbar';

const LoginForm = () => {
    const [credentials, setCredentials] = useState({ email: '', password: '' });
    const [role, setRole] = useState('');
    const [isLoading, setIsLoading] = useState(false); // State to control loading splash screen
    const history = useHistory();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setCredentials(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleAdminLogin = async () => {
        setIsLoading(true); // Show loading splash screen
        try {
            // Make Axios request specific to admin login
            const response = await axios.post('http://localhost:9898/admin/login', credentials);
            const token = response.data.data.token; // Extract token from response
            localStorage.setItem('token', token); // Store token in local storage
            console.log('Admin Login response:', response.data);
            history.push('/adminDashboard');
        } catch (error) {
            console.error('Admin Login error:', error);
            // Handle error
        } finally {
            setIsLoading(false); // Hide loading splash screen regardless of success or failure
        }
    };

    // Similar implementations for handleOwnerLogin and handleSecretaryLogin...

    const handleSubmit = async (event) => {
        event.preventDefault();

        if (role === 'admin') {
            await handleAdminLogin();
        } else if (role === 'owner') {
            await handleOwnerLogin();
        } else if (role === 'secretary') {
            await handleSecretaryLogin();
        }
    };

    return (
        <div className="login-container">
            {isLoading && <div className="loading-screen">Loading...</div>} {/* Loading splash screen */}
            <h1 className="login-form-h1">Login Form</h1>
            <form onSubmit={handleSubmit} className="login-form">
                <div className="login-form-group">
                    <label htmlFor="email" className="login-form-label">Email</label>
                    <input 
                        type="text" 
                        id="email" 
                        name="email"
                        value={credentials.email} 
                        onChange={handleChange} 
                        required 
                        className="login-form-control"
                    />
                </div>
                <div className="login-form-group">
                    <label htmlFor="password" className="login-form-label">Password</label>
                    <input 
                        type="password" 
                        id="password" 
                        name="password"
                        value={credentials.password} 
                        onChange={handleChange} 
                        required 
                        className="login-form-control"
                    />
                </div>
                <div className="login-form-group">
                    <label htmlFor="role" className="login-form-label">Role</label>
                    <select 
                        id="role" 
                        value={role} 
                        onChange={(e) => setRole(e.target.value)} 
                        required
                        className="login-form-control"
                    >
                        <option value="">Select Role</option>
                        <option value="admin">Admin</option>
                        <option value="owner">Owner</option>
                        <option value="secretary">Secretary</option>
                    </select>
                </div>
                <button type="submit" className="login-btn-submit">Login</button>
            </form>
        </div>
    );
};

export default LoginForm;
