import React, { useState } from 'react';
import axios from 'axios';
import './addAmenities.css'; // Import CSS file
import AmenitiesTable from '../table/amenitiesTable';

const AddAmenities = () => {
    const [name, setName] = useState('');
    const secretaryId = localStorage.getItem('secretaryId'); // Retrieve secretary ID from local storage

    const handleAddAmenity = async () => {
        try {
            const response = await axios.post('http://localhost:9898/amenities/addAmenities', {
                secretary_id: secretaryId,
                amenity_name: name
            });
            console.log(response.data);
            // Clear input field after successful submission
            setName('');
            alert('Amenity added successfully!');
        } catch (error) {
            console.error('Error adding amenity:', error);
            alert('Error adding amenity. Please try again.');
        }
    };

    return (
        <>
        <div className="add-amenities-container">
            <h2>Add Amenities</h2>
            <input
                type="text"
                placeholder="Enter Amenity Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                className="add-amenities-input"
            />
            <button onClick={handleAddAmenity} className="add-amenities-btn">Add Amenity</button>
        </div>

        <AmenitiesTable/>
        </>
    );
};

export default AddAmenities;
