import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ownerTable.css'; // Import the CSS file for styling

function OwnerTable() {
    const [ownerData, setOwnerData] = useState([]);
    const [error, setError] = useState(null);

    // Function to fetch owner data
    const fetchOwnerData = async () => {
        try {
            const response = await axios.get('http://localhost:9898/owner/getAllOwners');
            setOwnerData(response.data);
        } catch (error) {
            setError(error);
        }
    };

    // Fetch owner data when the component mounts
    useEffect(() => {
        fetchOwnerData();
    }, []);

    return (
        <div className="card">
            <h2 className="card-title">Owner Table </h2>
            <div className="table-container">
                <table className="owner-table">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Flat Number</th>
                            <th>Name</th>
                            <th>Address</th>
                            <th>Mobile No</th>
                            <th>Email</th>
                            <th>Password</th>
                        </tr>
                    </thead>
                    <tbody>
                        {ownerData.map(owner => (
                            <tr key={owner.id}>
                                <td>{owner.id}</td>
                                <td>{owner.flat_no}</td>
                                <td>{owner.name}</td>
                                <td>{owner.address}</td>
                                <td>{owner.mobile_no}</td>
                                <td>{owner.email}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                {error && <div>Error: {error.message}</div>}
            </div>
        </div>
    );
}

export default OwnerTable;
