// RegisterOwner.jsx
import { useHistory } from 'react-router-dom';
import React, { useState } from 'react';
import './registerSecretary.css'; // Import the CSS file
import Topbar from '../../components/topbar/Topbar';
import axios from 'axios';

function RegisterOwner() {
    const [registerOwner, setRegisterOwner] = useState(false);
    const [ownerData, setOwnerData] = useState({
        flat_no: '',
        name: '',
        address: '',
        mobile_no: '',
        email: '',
        password: ''
    });

    const history = useHistory();

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setOwnerData({ ...ownerData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem("token");
            const config = {
                headers: {
                    Authorization: `${token}`
                }
            };

            const response = await axios.post('http://localhost:9898/registerOwner', ownerData, config);
            console.log('Owner Registration Successful:', response.data);
            setRegisterOwner(true);
        } catch (error) {
            console.error('Owner Registration Error:', error);
        }
    };

    useEffect(() => {
        if (registerOwner) {
            history.push('/dashboard'); // Redirect to dashboard after successful registration
        }
    }, [registerOwner, history]);

    return (
        <div className="register-secretary-container">
            <Topbar/>
            <h2 className='form-label'>Register Owner</h2>
            <form onSubmit={handleSubmit} className="register-secretary-form"> {/* Apply the same CSS class */}
                <div className="form-group">
                    <label htmlFor="flat_no">Flat Number</label>
                    <input 
                        type="text" 
                        id="flat_no" 
                        name="flat_no"
                        value={ownerData.flat_no} 
                        onChange={handleInputChange} 
                        required 
                        className="form-control"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="name">Name</label>
                    <input 
                        type="text" 
                        id="name" 
                        name="name"
                        value={ownerData.name} 
                        onChange={handleInputChange} 
                        required 
                        className="form-control"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="address">Address</label>
                    <input 
                        type="text" 
                        id="address" 
                        name="address"
                        value={ownerData.address} 
                        onChange={handleInputChange} 
                        required 
                        className="form-control"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="mobile_no">Mobile No</label>
                    <input 
                        type="text" 
                        id="mobile_no" 
                        name="mobile_no"
                        value={ownerData.mobile_no} 
                        onChange={handleInputChange} 
                        required 
                        className="form-control"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="email">Email</label>
                    <input 
                        type="text" 
                        id="email" 
                        name="email"
                        value={ownerData.email} 
                        onChange={handleInputChange} 
                        required 
                        className="form-control"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Password</label>
                    <input 
                        type="password" 
                        id="password" 
                        name="password"
                        value={ownerData.password} 
                        onChange={handleInputChange} 
                        required 
                        className="form-control"
                    />
                </div>
                <button type="submit" className="btn-submit">Register</button>
            </form>
        </div>
    );
}

export default RegisterOwner;
