import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom'; // Import useHistory hook
import './amenitiesTable.css';

const AmenitiesTable = () => {
    const [amenities, setAmenities] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const history = useHistory(); // Get the history object

    useEffect(() => {
        // Fetch amenities data when the component mounts
        fetchAmenities();
    }, []);

    const fetchAmenities = async () => {
        try {
            const response = await axios.get('http://localhost:9898/amenities'); // Adjust the URL accordingly
            setAmenities(response.data.data);
            setLoading(false);
        } catch (error) {
            setError(error);
            setLoading(false);
        }
    };

    const handleAddAmenities = async () => {
        try {
            // Add amenities logic here
            // Once added successfully, navigate to Secretary Dashboard
            history.push('/secretaryDashboard'); // Redirect to Secretary Dashboard route
        } catch (error) {
            setError(error);
        }
    };

    return (
        <div className="amenitiesTable-card">
            <h2 className="amenitiesTable-title">Amenities Table</h2>
            {loading ? (
                <div>Loading...</div>
            ) : error ? (
                <div>Error: {error.message}</div>
            ) : (
                <div>
                    <div className="amenitiesTable-container">
                        <table className="amenitiesTable-table">
                            {/* Table content */}
                        </table>
                    </div>
                    <button onClick={handleAddAmenities}>Add Amenities</button>
                </div>
            )}
        </div>
    );
};

export default AmenitiesTable;
