import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Table.css'; // Import CSS file from SocietyTable component
import { RemoveCircleOutlineOutlined } from '@mui/icons-material';

const OwnerTable = () => {
  const [owners, setOwners] = useState([]);
  const [error, setError] = useState(null);

  // Define function to fetch owner data
  const fetchOwners = () => {
    axios.get('http://localhost:9898/owner/owners')
      .then(response => {
        setOwners(response.data);
      })
      .catch(error => {
        setError(error);
      });
  };

  // Call the fetchOwners function when the component mounts
  useEffect(() => {
    fetchOwners();
  }, []);

  // Function to handle removal of an owner
  const handleRemoveOwner = (id) => {
    axios.delete(`http://localhost:9898/owner/deleteOwner/${id}`)
      .then(() => {
        // Reload the page to reflect the updated data
        window.location.reload();
      })
      .catch(error => {
        console.error('Error removing owner:', error);
        setError(error);
      });
  };

  return (
    <div className="card1">
      <h2 className="card-title">Owner Table</h2>
      <div className="table-container">
        <table className="society-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Address</th>
              <th>Mobile No</th>
              <th>Email</th>
              <th>Remove</th>
            </tr>
          </thead>
          <tbody>
            {owners.map(owner => (
              <tr key={owner.id}>
                <td>{owner.id}</td>
                <td>{owner.name}</td>
                <td>{owner.address}</td>
                <td>{owner.mobile_no}</td>
                <td>{owner.email}</td>
                <td>
                  {/* Add onClick event to the remove icon */}
                  <RemoveCircleOutlineOutlined onClick={() => handleRemoveOwner(owner.id)} />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        {error && <div>Error: {error.message}</div>}
      </div>
    </div>
  );
};

export default OwnerTable;
