import React, { useState } from 'react';
import { Redirect } from 'react-router-dom'; // Import Redirect from react-router-dom
import './login.css'; // Import the CSS file for styling

const LoginForm = () => {
    const [credentials, setCredentials] = useState({ email: '', password: '' });
    const [role, setRole] = useState('');
    const [redirect, setRedirect] = useState(null); // State to manage redirection

    const handleChange = (e) => {
        const { name, value } = e.target;
        setCredentials(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        // You can add your login logic here
        console.log('Credentials:', credentials);
        console.log('Role:', role);

        // Reset form fields after submission
        setCredentials({ email: '', password: '' });
        setRole('');

        // Redirect based on role
        if (role === 'admin') {
            setRedirect('/adminDashboard'); // Set the redirect path to admin dashboard
        } else if (role === 'owner') {
            setRedirect('/owner-dashboard'); // Set the redirect path to owner dashboard
        } else if (role === 'secretary') {
            setRedirect('/secretary-dashboard'); // Set the redirect path to secretary dashboard
        }
    };

    // If redirect is set, render Redirect component
    if (redirect) {
        return <Redirect to={redirect} />;
    }

    return (
        <div className="login-container">
            <label className="form-label">Login Form</label>
            <form onSubmit={handleSubmit} className="login-form">
                <div className="form-group">
                    <label htmlFor="email">Email</label>
                    <input 
                        type="text" 
                        id="email" 
                        name="email"
                        value={credentials.email} 
                        onChange={handleChange} 
                        required 
                        className="form-control"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Password</label>
                    <input 
                        type="password" 
                        id="password" 
                        name="password"
                        value={credentials.password} 
                        onChange={handleChange} 
                        required 
                        className="form-control"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="role">Role</label>
                    <select 
                        id="role" 
                        value={role} 
                        onChange={(e) => setRole(e.target.value)} 
                        required
                        className="form-control"
                    >
                        <option value="">Select Role</option>
                        <option value="admin">Admin</option>
                        <option value="owner">Owner</option>
                        <option value="secretary">Secretary</option>
                    </select>
                </div>
                <button type="submit" className="btn-submit">Login</button>
            </form>
        </div>
    );
};

export default LoginForm;
