import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './adminHome.css';
import SocietyTable from '../table/societyTable';
import ManIcon from '@mui/icons-material/Man';
import ApartmentIcon from '@mui/icons-material/Apartment';
import { Link } from '@mui/material';

function AdminHome() {
    const [societiesCount, setSocietiesCount] = useState(0);
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    useEffect(() => {
        // Fetch societies count when the component mounts
        fetchSocietiesCount();
    }, []);

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
      };

      const handleLogout = () => {
        // Handle logout logic here
        // For example, clear session/local storage, etc.
        // Then navigate to the home page
        localStorage.removeItem('token')
        history.push('/');
      };


    const fetchSocietiesCount = async () => {
        try {
            const response = await axios.get('http://localhost:9898/society/getAllSocietiesCount');
            setSocietiesCount(response.data.count);
        } catch (error) {
            console.error('Error fetching societies count:', error);
        }
    };

    return (
        <>
            <div className="home-container">

                <div className="card">
                    <h2>Societies</h2>
                    <p>{societiesCount}</p>
                </div>


            <div className="card">    
            <img
            src="https://www.svgrepo.com/show/384674/account-avatar-profile-user-11.svg"
            alt=""
            className="topAvatar"
            onClick={toggleMenu} // Toggle menu on image click
          />
          {isMenuOpen && (
            <div className="menu">
              <ul>
                <li>Profile</li>
                <li>Settings</li>
                <li onClick={handleLogout}>Logout</li> {/* Call handleLogout on click */}
              </ul>
            </div>
          )}
              </div>


                <div className="card">
                    <h2>Happy Families</h2>
                    <p>465</p>
                </div>



            </div>



            <SocietyTable />
        </>
    );
}

export default AdminHome;
