// RegisterSecretary.jsx
import { useHistory } from 'react-router-dom'; // Import useHistory hook
import React, { useState } from 'react';
import './registerSecretary.css';
import Topbar from '../../components/topbar/Topbar';
import { useEffect } from 'react';
import axios from 'axios'
function RegisterSecretary() {
    const [registerSec, setRegisterSec] = useState(false);
    const [secretaryData, setSecretaryData] = useState({
      society_id: '',
      name: '',
      email: '',
      mobile_no: '',
      password: ''
    });

    const history = useHistory();

    const handleInputChange = (e) => {
      const { name, value } = e.target;
      setSecretaryData({ ...secretaryData, [name]: value });
    };

    const handleSubmit = async (e) => {
      e.preventDefault();
      try {
        const token = localStorage.getItem("token");
        const config = {
          headers: {
            Authorization: `Bearer ${token}` // Assuming you're using Bearer token authentication
          }
        };

        const response = await axios.post('http://localhost:9898/secretary/registerSecretary', secretaryData, config);
        console.log('Secretary Registration Successful:', response.data);
        setRegisterSec(true);
      } catch (error) {
        console.error('Secretary Registration Error:', error);
      }
    };

    useEffect(() => {
      if (registerSec) {
        // Redirect to '/secretaryDashboard' if registerSec is true
        history.push('/adminDashboard');
      }
    }, [registerSec, history]);


    return (
        <div className="register-secretary-container">
               <Topbar/>
            <h2 className='form-label'>Register Secretary</h2>
            <form onSubmit={handleSubmit} className="register-secretary-form">
                <div className="form-group">
                    <label htmlFor="society_id">Society ID</label>
                    <input 
                        type="text" 
                        id="society_id" 
                        name="society_id"
                        value={secretaryData.society_id} 
                        onChange={handleInputChange} 
                        required 
                        className="form-control"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="name">Name</label>
                    <input 
                        type="text" 
                        id="name" 
                        name="name"
                        value={secretaryData.name} 
                        onChange={handleInputChange} 
                        required 
                        className="form-control"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="email">Email</label>
                    <input 
                        type="text" 
                        id="email" 
                        name="email"
                        value={secretaryData.email} 
                        onChange={handleInputChange} 
                        required 
                        className="form-control"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="mobile_no">Mobile No</label>
                    <input 
                        type="text" 
                        id="mobile_no" 
                        name="mobile_no"
                        value={secretaryData.mobile_no} 
                        onChange={handleInputChange} 
                        required 
                        className="form-control"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Password</label>
                    <input 
                        type="password" 
                        id="password" 
                        name="password"
                        value={secretaryData.password} 
                        onChange={handleInputChange} 
                        required 
                        className="form-control"
                    />
                </div>
                <button type="submit" className="btn-submit">Register</button>
            </form>
        </div>
    );
};

export default RegisterSecretary;
