import React, { useState } from 'react';
import './Navbar.css'; // Import your CSS file for styling
import { Link } from 'react-router-dom'; // Import Link from react-router-dom

function Navbar() {
  const [isLoading, setIsLoading] = useState(false); // State to control loading splash screen

  // Function to handle login link click
  const handleLoginClick = () => {
    setIsLoading(true); // Show loading splash screen
    // Simulate loading process (You can replace setTimeout with actual API call)
    setTimeout(() => {
      // After loading is complete, you can navigate to the login page or perform any other action
      setIsLoading(false); // Hide loading splash screen
    }, 2000); // Simulating 2 seconds of loading time
  };

  return (
    <nav className="navbar">
      <div className="container">
        <Link to="/" className="logo">Sostfy</Link> {/* Update this line */}
        <ul className="nav-links">
          <li><Link to="/login" onClick={handleLoginClick}>Login</Link></li>
          {/* Add more list items for additional navbar links */}
        </ul>
      </div>
      {isLoading && <div className="loading-screen">Loading...</div>} {/* Loading splash screen */}
    </nav>
  );
}

export default Navbar;
