import React, { useState, useEffect } from 'react';
import { Redirect } from 'react-router-dom'; // Import Redirect component
import './login.css'; // Importing CSS file for styling

const LoginForm = () => {
  const [redirectTo, setRedirectTo] = useState(null); // State to manage redirection
  const [role, setRole] = useState('');
  const [credentials, setCredentials] = useState({ email: '', password: '' });

  const handleRoleChange = (e) => {
    setRole(e.target.value);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCredentials({ ...credentials, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you can perform your login logic
    console.log('Role:', role);
    console.log('Credentials:', credentials);

    // Redirect based on role
    switch (role) {
      case 'admin':
        setRedirectTo('/admin-dashboard'); // Set redirection path
        break;
      // Add cases for other roles if needed
      default:
        // Handle default case
    }

    // Reset form fields
    setRole('');
    setCredentials({ email: '', password: '' });
  };

  // Redirect the user when redirectTo state changes
  useEffect(() => {
    if (redirectTo) {
      // Reset redirectTo state after redirecting
      setRedirectTo(null);
    }
  }, [redirectTo]);

  // Render Redirect component if redirectTo is set
  if (redirectTo) {
    return <Redirect to={redirectTo} />;
  }

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="role">Role:</label>
          <select id="role" value={role} onChange={handleRoleChange}>
            <option value="">Select Role</option>
            <option value="admin">Admin</option>
            {/* Add options for other roles if needed */}
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input type="email" id="email" name="email" value={credentials.email} onChange={handleInputChange} />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input type="password" id="password" name="password" value={credentials.password} onChange={handleInputChange} />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default LoginForm;
