import { useState, useEffect } from "react";
import { DataGrid } from '@mui/x-data-grid';
import DeleteIcon from '@mui/icons-material/Delete';
import { Link } from "react-router-dom";
import axios from "axios";
import './userList.css';

export default function UserList() {
    const [users, setUsers] = useState([]);


    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const response = await axios.get("http://localhost:4001/user");
                console.log("API Response:", response.data); // Log the response data
                setUsers(response.data.data || []); // Set to an empty array if data is undefined
            } catch (error) {
                console.error("Error fetching users:", error);
            }
        };
        fetchUsers();
    }, []);


    const handleDelete = (id) => {
        setUsers(users.filter((user) => user.id !== id));
    };

    const columns = [
        { field: 'id', headerName: 'ID', width: 70 },
        { field: 'email', headerName: 'Email', width: 200 },
        { field: 'firstName', headerName: 'First name', width: 130 },
        { field: 'lastName', headerName: 'Last name', width: 130 },
        { field: 'phone', headerName: 'Phone', width: 130 },
        {
            field: 'action',
            headerName: 'Action',
            width: 150,
            renderCell: (params) => (
                <>
                    <Link to={`/user/id${params.row.id}`}>
                        <button className="userListEdit">Edit</button>
                    </Link>
                    <DeleteIcon
                        className="userListDelete"
                        onClick={() => handleDelete(params.row.id)}
                    />
                </>
            ),
        },
    ];

    return (
        <div className="userList" style={{ height: 400, width: '100%' }}>
            <DataGrid
                rows={users}
                columns={columns}
                pageSize={5}
                rowsPerPageOptions={[5, 10]}
                checkboxSelection
                disableSelectionOnClick
            />
        </div>
    );
}
