import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ownerTable.css'; // Import the CSS file for styling
import { RemoveCircleOutlineOutlined } from '@mui/icons-material';

function OwnerTable() {
    const [ownerData, setOwnerData] = useState([]);
    const [error, setError] = useState(null);
    const [isLoading, setIsLoading] = useState(true);

    // Function to fetch owner data
    const fetchOwnerData = async () => {
        try {
            const response = await axios.get('http://localhost:9898/owner/owners');
            setOwnerData(response.data.results); // Assuming the data is stored in the 'results' key
            setIsLoading(false);
        } catch (error) {
            setError(error);
            setIsLoading(false);
        }
    };

    // Fetch owner data when the component mounts
    useEffect(() => {
        fetchOwnerData();
    }, []);

    // Function to handle removal of an owner
    const handleRemoveOwner = async (id) => {
        try {
            await axios.delete(`http://localhost:9898/owner/deleteOwner/${id}`);
            // Filter out the removed owner from the state
            setOwnerData(ownerData.filter(owner => owner.id !== id));
        } catch (error) {
            console.error('Error removing owner:', error);
            setError(error);
        }
    };

    return (
        <div className="card">
            <h2 className="card-title">Owner Table</h2>
            <div className="table-container">
                {isLoading ? (
                    <div>Loading...</div>
                ) : (
                    <table className="owner-table">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Flat Number</th>
                                <th>Name</th>
                                <th>Address</th>
                                <th>Mobile No</th>
                                <th>Email</th>
                                <th>Password</th>
                                <th>Remove</th>
                            </tr>
                        </thead>
                        <tbody>
                            {ownerData.map(owner => (
                                <tr key={owner.id}>
                                    <td>{owner.id}</td>
                                    <td>{owner.flat_no}</td>
                                    <td>{owner.name}</td>
                                    <td>{owner.address}</td>
                                    <td>{owner.mobile_no}</td>
                                    <td>{owner.email}</td>
                                    <td>{owner.password}</td>
                                    <td>
                                        {/* Add onClick event to the remove icon */}
                                        <RemoveCircleOutlineOutlined onClick={() => handleRemoveOwner(owner.id)} />
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
                {error && <div>Error: {error.message}</div>}
            </div>
        </div>
    );
}

export default OwnerTable;
