import React, { useEffect } from 'react';
import ManIcon from '@mui/icons-material/Man';
import HomeIcon from '@mui/icons-material/Home';
import ApartmentIcon from '@mui/icons-material/Apartment';
import { Link, useHistory } from "react-router-dom";
import "./sidebar.css"; // Import the CSS file for sidebar styles

export default function Sidebar() {
    const history = useHistory();

    useEffect(() => {
        // Redirect to the admin dashboard only if the current location is not '/about'
        if (history.location.pathname !== '/about') {
            history.push('/adminDashboard');
        }
    }, []); // Run the effect only once when the component mounts


    return (
        <div className="sidebar">

            <div className="sidebarWrapper">

                <div className="sidebarMenu">
                    <h3 className="sidebarTitle">Dashboard</h3>
                    <ul className="sidebarList">
                        <li className="sidebarListItem active">
                            <HomeIcon/>
                            Home
                        </li>
                    </ul>
                </div>

                <div className="sidebarMenu">
                    <ul className="sidebarList">
                        <Link to="/about" className="link" style={{ textDecoration: 'none' }}>
                            <li className="sidebarListItem">
                                <ApartmentIcon/>
                                About Us
                            </li>
                        </Link>
                    </ul>

                    <div className="sidebarMenu">
                        <ul className="sidebarList">
                            <Link to="/contact" className="link" style={{ textDecoration: 'none' }}>
                                <li className="sidebarListItem">
                                    <ManIcon/>
                                    Contact Us
                                </li>
                            </Link>
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    );
}
