import React from 'react';
import './about.css'; // Import CSS file for styling
import Topbar from './components/topbar/Topbar';

const About = () => {
  return (
    <>
  <Topbar/>
      <div className="about-container"> {/* Change the class name to .about-container */}
        <h4>About Us</h4> {/* No need to add a class here, as it's styled by .about-container h4 */}
        <div className="c-container"> {/* You may remove this div if it's not necessary */}
          <p>
            Our Society Management System is a comprehensive software solution dedicated to facilitating community engagement and welfare. We provide a platform that fosters a welcoming environment for all members and enables effective management of various aspects of community living.
          </p>
        </div>
      </div>
    </>
  );
};

export default About;
