import React, { useEffect } from 'react';
import ManIcon from '@mui/icons-material/Man';
import HomeIcon from '@mui/icons-material/Home';
import ApartmentIcon from '@mui/icons-material/Apartment';
import { Link, useHistory } from "react-router-dom";
import "./sidebar.css"; // Import the CSS file for sidebar styles

export default function Sidebar() {
    const history = useHistory();

    useEffect(() => {
        // Redirect to the admin dashboard when the sidebar component mounts
        history.push('/adminDashboard');
    }, [history]);

    return (
        <div className="sidebar">

            <div className="sidebarWrapper">

                <div className="sidebarMenu">
                    <h3 className="sidebarTitle">Dashboard</h3>
                    <ul className="sidebarList">
                        <li className="sidebarListItem active">
                            <HomeIcon/>
                            Home
                        </li>
                    </ul>
                </div>

                <div className="sidebarMenu">
                    <h3 className="sidebarTitle">Society</h3>
                    <ul className="sidebarList">
                        <Link to="/registerSociety" className="link" style={{ textDecoration: 'none' }}>
                            <li className="sidebarListItem">
                                <ApartmentIcon/>
                                About Us
                            </li>
                        </Link>
                    </ul>

                    <div className="sidebarMenu">
                        <h3 className="sidebarTitle">Secretary</h3>
                        <ul className="sidebarList">
                            <Link to="/registerSecretary" className="link" style={{ textDecoration: 'none' }}>
                                <li className="sidebarListItem">
                                    <ManIcon/>
                                    Register Secretary
                                </li>
                            </Link>
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    );
}
